import React from 'react'
import { Form, Segment, Button, Grid, Message, GridColumn, FormInput } from "semantic-ui-react";
import style from "./login.module.css";

const Login = () => {

    const handleSubmit = event => {
        event.preventDefault();//form her yenilendiğinde sayfa yenilenmesini engellmemek için 
    }

    return (
        <Grid textAlign="center" verticalAlign="middle" className={style.container}>
            <Grid.Column style={{ maxWidth: 450 }}>
                <h1 className={style.formHeader}>
                    Chatify
            <span>.io</span>
                </h1>
                <Form
                    size="large"
                    className={style.form}
                    onSubmit={handleSubmit}
                >
                    <Segment>
                        <FormInput fluid icon="mail" iconPosition="left" name="email" placeholder="Email Adresi" type="email" />
                        <FormInput fluid icon="lock" iconPosition="left" name="password" placeholder="Şifre" type="password" />
                        <Button color="purple" fluid size="large" >
                            Giriş Yap
                        </Button>
                    </Segment>
                </Form>
            </Grid.Column>
        </Grid>
    )
}

export default Login;
