import React,{useEffect} from 'react'
import { Link } from 'react-router-dom';
import { Form, Segment, Button, Grid, Message, GridColumn, FormInput } from "semantic-ui-react";
import style from "./signup.module.css";
import {useForm} from "react-hook-form";

const SignUp = () => {

    const {register,errors,handleSubmit,setValue}=useForm();

    useEffect(() => {
        register({name:"username"},{required:true,minLength:3});
        register({name:"password"},{required:true,minLength:6});
        register({name:"email"},{required:true});
    }, [])

    const onSubmit = (data,e) => {
        //event.preventDefault();//form her yenilendiğinde sayfa yenilenmesini engellmemek için 
        console.log(data);
    }

    return (
        <Grid textAlign="center" verticalAlign="middle" className={style.container}>
            <GridColumn style={{ maxWidth: 450 }}>
                <h1 className={style.formHeader}>
                    Chatify
            <span>.io</span>
                </h1>
                <Form
                    size="large"
                    className={style.form}
                    onSubmit={handleSubmit(onSubmit)}
                >
                    <Segment>
                        <FormInput fluid icon="user" iconPosition="left" name="username" placeholder="Kullanıcı Adı" type="text" onChange={(event,{name,value})=>{setValue(name,value)}}/>
                        <FormInput fluid icon="mail" iconPosition="left" name="email" placeholder="Email Adresi" type="email" onChange={(event,{name,value})=>{setValue(name,value)}}/>
                        <FormInput fluid icon="lock" iconPosition="left" name="password" placeholder="Şifre" type="password" onChange={(event,{name,value})=>{setValue(name,value)}}/>
                        <Button color="purple" fluid size="large" >
                            Hesap Oluştur
                        </Button>
                    </Segment>
                </Form>
                <Message>
                     {/* buraya tıklanınca url i /signup yapar ve /signup a yönlendirme gerçekleşir */}
                     Zaten bir hesabın var mı ? <Link to="/login">Giriş Yap</Link>
                </Message>
              
            </GridColumn>
        </Grid>
    )
}

export default SignUp;
