import React from 'react'
import { Link } from 'react-router-dom';
import { Form, Segment, Button, Grid, Message, GridColumn, FormInput } from "semantic-ui-react";
import style from "./signup.module.css";

const SignUp = () => {

    const handleSubmit = event => {
        event.preventDefault();//form her yenilendiğinde sayfa yenilenmesini engellmemek için 
    }

    return (
        <Grid textAlign="center" verticalAlign="middle" className={style.container}>
            <GridColumn style={{ maxWidth: 450 }}>
                <h1 className={style.formHeader}>
                    Chatify
            <span>.io</span>
                </h1>
                <Form
                    size="large"
                    className={style.form}
                    onSubmit={handleSubmit}
                >
                    <Segment>
                        <FormInput fluid icon="user" iconPosition="left" name="username" placeholder="Kullanıcı Adı" type="text"/>
                        <FormInput fluid icon="mail" iconPosition="left" name="email" placeholder="Email Adresi" type="email" />
                        <FormInput fluid icon="lock" iconPosition="left" name="password" placeholder="Şifre" type="password" />
                        <Button color="purple" fluid size="large" >
                            Hesap Oluştur
                        </Button>
                    </Segment>
                </Form>
                <Message>
                     {/* buraya tıklanınca url i /signup yapar ve /signup a yönlendirme gerçekleşir */}
                     Zaten bir hesabın var mı ? <Link to="/login"></Link>
                </Message>
              
            </GridColumn>
        </Grid>
    )
}

export default SignUp;
